import 'package:flutter/material.dart';import '../../data/Articles.dart';import '../view_new.dart';class BuildArticles extends StatelessWidget {  const BuildArticles({    super.key,    required this.articles,  });  final Articles articles;  @override  Widget build(BuildContext context) {    return Container(      color: Colors.black12,      margin: const EdgeInsets.symmetric(horizontal: 15, vertical: 10),      child: ListTile(        onTap: () => {          Navigator.of(context).push(MaterialPageRoute(            builder: (context) => ViewNew(              model: articles,            ),          ))        },        title: Text(          articles.title ?? "No Title",          style: Theme.of(context)              .textTheme              .bodyLarge!              .copyWith(fontWeight: FontWeight.bold),          maxLines: 2,          overflow: TextOverflow.ellipsis,        ),        subtitle: Row(          children: [            Expanded(              child: Text(                articles.description ?? "No Description",                style: Theme.of(context).textTheme.bodySmall,                maxLines: 1,                overflow: TextOverflow.ellipsis,              ),            ),          ],        ),        trailing: articles.urlToImage != null            ? Image.network(                articles.urlToImage!,                width: 100,                height: 60,                fit: BoxFit.cover,                errorBuilder: (context, error, stackTrace) =>                    const Icon(Icons.broken_image),              )            : const Icon(Icons.image_not_supported),      ),    );  }}